import time
import sys
import ctypes
import win32gui
import base64
import cv2
import numpy as np
import pyautogui
from PyQt5.QtWidgets import (QApplication, QWidget, QVBoxLayout, QTextEdit,
                             QPushButton, QLabel, QHBoxLayout, QLineEdit)
from PyQt5.QtCore import QThread, pyqtSignal, QTimer, Qt, QEventLoop
from PyQt5.QtGui import QColor

# ================= 把 4 张 PNG 的 base64 粘到这里 =================
IMAGE_1_BASE64 = ''   # 准备按钮
IMAGE_2_BASE64 = ''   # 下载按钮
IMAGE_3_BASE64 = ''   # 开始按钮
IMAGE_4_BASE64 = ''   # 优先级按钮
# ================================================================

# ---------- 管理员 ----------
def is_admin():
    try:
        return ctypes.windll.shell32.IsUserAnAdmin()
    except:
        return False
def run_as_admin():
    try:
        ctypes.windll.shell32.ShellExecuteW(None, "runas", sys.executable, " ".join(sys.argv), None, 1)
    except:
        print("无法提权")
        sys.exit(1)
if not is_admin():
    run_as_admin()

# ---------- CUDA ----------
try:
    cuda_enabled = cv2.cuda.getCudaEnabledDeviceCount() > 0
except:
    cuda_enabled = False

# ---------- base64 → cv2 Mat ----------
def base64_to_cv_mat(b64_str):
    if not b64_str:
        return None
    b64_str += "=" * ((4 - len(b64_str) % 4) % 4)
    img_data = base64.b64decode(b64_str)
    nparr = np.frombuffer(img_data, np.uint8)
    return cv2.imdecode(nparr, cv2.IMREAD_GRAYSCALE)

# ---------- 日志 ----------
class LogSignal(QThread):
    log_updated = pyqtSignal(str)
    def output_log(self, msg):
        self.log_updated.emit(msg)

# ---------- 主窗口 ----------
class MainWindow(QWidget):
    def __init__(self):
        super().__init__()
        self.log_signal = LogSignal()
        self.init_ui()
        self.log_signal.log_updated.connect(self.update_log)
        self.setFocusPolicy(Qt.StrongFocus)
        self.is_running = False
        self.has_shown_gpu_info = False

    def init_ui(self):
        self.setWindowTitle('Forts自动准备|开始')
        self.setGeometry(100, 100, 500, 300)
        lay = QVBoxLayout(self)

        self.log_text = QTextEdit(readOnly=True)
        self.log_text.setMinimumHeight(200)
        lay.addWidget(QLabel('日志'))
        lay.addWidget(self.log_text)

        h = QHBoxLayout()
        label = QLabel('运行间隔(秒) [至少大于5秒]')
        label.setMaximumWidth(179)  # 限制标签宽度
        h.addWidget(label)
        self.interval_input = QLineEdit('5')
        self.interval_input.setMaximumWidth(50)
        h.addWidget(self.interval_input)
        self.gpu_btn = QPushButton('GPU加速 开')
        self.gpu_btn.setEnabled(cuda_enabled)
        self.gpu_btn.clicked.connect(self.toggle_gpu)
        h.addWidget(self.gpu_btn)
        lay.addLayout(h)

        h2 = QHBoxLayout()
        self.start_btn = QPushButton('开始挂机')
        self.start_btn.clicked.connect(self.start_or_stop)
        h2.addWidget(self.start_btn)
        quit_btn = QPushButton('退出')
        quit_btn.clicked.connect(self.close)
        h2.addWidget(quit_btn)
        lay.addLayout(h2)

    def toggle_gpu(self):
        global cuda_enabled
        if cuda_enabled:
            cuda_enabled = not cuda_enabled
            self.gpu_btn.setText('强制使用GPU加速' if cuda_enabled else 'GPU加速 开')
            self.output_log('强制使用GPU加速' if cuda_enabled else '关闭GPU加速')

    def update_log(self, txt):
        color = Qt.black
        if '已开始' in txt:
            color = Qt.green
        elif '已暂停' in txt or '注意' in txt:
            color = Qt.red
        elif '提示' in txt:
            color = QColor(35, 80, 150)
        self.log_text.setTextColor(color)
        self.log_text.append(txt)
        self.log_text.setTextColor(Qt.black)

    def output_log(self, msg):
        self.log_signal.output_log(msg)

    def keyPressEvent(self, ev):
        if ev.key() == Qt.Key_F12:
            self.start_or_stop()

    def start_or_stop(self):
        if not self.is_running:
            self.clicker = AutoClicker(self)
            self.clicker.start()
            self.is_running = True
            self.start_btn.setText('停止挂机')
            self.gpu_btn.setEnabled(False)
            self.output_log('已开始')
        else:
            self.clicker.stop()
            self.is_running = False
            self.start_btn.setText('开始挂机')
            if cuda_enabled:
                self.gpu_btn.setEnabled(True)
            self.output_log('已暂停')

    def get_interval(self):
        try:
            v = int(self.interval_input.text())
            return max(v, 5)
        except:
            return 5

# ---------- 图像识别 ----------
class ImageFinder:
    _name_map = {
        '1.png': '准备按钮',
        '2.png': '下载按钮',
        '3.png': '开始按钮',
    }

    def __init__(self, logger):
        self.logger = logger

    def find_image_with_feature_matching(self, target_mat):
        if target_mat is None:
            return None
        screen = cv2.cvtColor(np.array(pyautogui.screenshot()), cv2.COLOR_RGB2GRAY)

        best_sim = -1
        best = None
        for scale in np.linspace(0.3, 2.5, 10):
            resized = cv2.resize(target_mat, None, fx=scale, fy=scale)
            if resized.shape[0] > screen.shape[0] or resized.shape[1] > screen.shape[1]:
                continue
            res = cv2.matchTemplate(screen, resized, cv2.TM_CCOEFF_NORMED)
            _, max_val, _, max_loc = cv2.minMaxLoc(res)
            if max_val > best_sim:
                best_sim = max_val
                h, w = resized.shape
                best = {
                    'pts': np.array([
                        [[max_loc[0], max_loc[1]]],
                        [[max_loc[0], max_loc[1]+h]],
                        [[max_loc[0]+w, max_loc[1]+h]],
                        [[max_loc[0]+w, max_loc[1]]]
                    ], dtype=np.float32),
                    'sim': max_val,
                    'scale': scale
                }
        return best if best and best['sim'] >= 0.70 else None

    def find_and_click_image(self, name, mats):
        target_mat = mats[name]
        res = self.find_image_with_feature_matching(target_mat)

        btn = self._name_map.get(name, name)
        if res is None:
            # 未命中
            print(f'[{time.strftime("%H:%M:%S")}] 未找到【{btn}】')
            return False

        sim   = res['sim']
        scale = res['scale']

        # 1.png 额外判断 4.png
        if name == '1.png':
            mat4 = mats.get('4.png')
            if mat4 is not None:
                r4 = self.find_image_with_feature_matching(mat4)
                if r4 and r4['sim'] > sim:
                    print(f'[{time.strftime("%H:%M:%S")}] 4.png({r4["sim"]:.2f}) > 1.png({sim:.2f}) → 跳过')
                    return False

        # 真正点击
        pts = res['pts']
        cx  = int((pts[0][0][0] + pts[2][0][0]) / 2)
        cy  = int((pts[0][0][1] + pts[2][0][1]) / 2)
        print(f'[{time.strftime("%H:%M:%S")}] 发现【{btn}】(相似度 {sim:.2f}, 缩放 {scale:.2f}) → 点击({cx}, {cy})')
        self.logger(f"【{btn}】已点击 ({cx}, {cy})")

        hwnd = win32gui.GetForegroundWindow()
        if hwnd:
            win32gui.SetForegroundWindow(hwnd)
            time.sleep(0.1)
            pyautogui.click(cx, cy)
        return True

# ---------- 自动点击线程 ----------
class AutoClicker(QThread):
    def __init__(self, win):
        super().__init__()
        self.win = win
        self.running = True
        self.timer = None

    def run(self):
        mats = {
            '1.png': base64_to_cv_mat(IMAGE_1_BASE64),
            '2.png': base64_to_cv_mat(IMAGE_2_BASE64),
            '3.png': base64_to_cv_mat(IMAGE_3_BASE64),
            '4.png': base64_to_cv_mat(IMAGE_4_BASE64),
        }

        if not self.win.has_shown_gpu_info:
            self.win.output_log('检测到CUDA，可启用GPU加速' if cuda_enabled else '未检测到CUDA，不支持GPU加速')
            self.win.output_log('注意：请勿遮挡游戏窗口')
            self.win.output_log('提示：管理员模式运行可按F12可开关程序')
            self.win.output_log('该程序由哔哩哔哩“简朴无谓”制作')
            self.win.has_shown_gpu_info = True

        finder = ImageFinder(self.win.output_log)
        self.timer = QTimer()
        self.timer.timeout.connect(lambda: self.check_all(finder, mats))
        self.timer.start(self.win.get_interval() * 1000)
        loop = QEventLoop()
        loop.exec_()

    def check_all(self, finder, mats):
        if not self.running:
            self.timer.stop()
            return
        for name in ['1.png', '2.png', '3.png']:
            finder.find_and_click_image(name, mats)

    def stop(self):
        self.running = False
        if self.timer:
            self.timer.stop()

# ---------- 入口 ----------
if __name__ == '__main__':
    app = QApplication(sys.argv)
    w = MainWindow()
    w.show()
    sys.exit(app.exec_())